mapper.select:
	select nonum,notitle, nocontent, to_char(nowriteDay,'YYYY/MM/dd') nowriteDay, noreadCnt
    from notice
    where nonum = #{nonum}

mapper.update:
update notice
set noreadCnt = noreadCnt + 1
where nonum = #{nonum}

update notice
set notitle=#{notitle} , nocontent=#{nocontent}
where nonum = #{nonum}
