mapper.select:
select count(*)
from stay

	
		stayTitle LIKE '%'||#{staySearchValue}||'%'
	
	
		stayName LIKE '%'||#{staySearchValue}||'%'
	
	
		and stayLocation =#{stayLocation}
	
	
		and stayType =#{stayType}
	
	
		and stayGrade =#{stayGrade}

select stayNum,stayName,stayImage,stayPhone, stayLocation,stayAdultPrice,stayKidPrice,stayGrade,stayTitle,stayContent, to_char(stayWriteDay,'YYYY/MM/dd') stayWriteDay,stayReadCnt, stayRepleCnt,staySite,stayGoods,stayType,entNum,stayImageClone from stay
stayTitle LIKE '%'||#{staySearchValue}||'%'
stayName LIKE '%'||#{staySearchValue}||'%'
and stayLocation =#{stayLocation}
and stayType =#{stayType}
and stayGrade =#{stayGrade}
order by stayNum desc
order by stayGoods desc
order by stayWriteDay desc

mapper.update:
update stay set
stayImage=#{stayImage}, stayImageClone=#{stayImageClone},
stayContent=#{stayContent}, stayName=#{stayName}, stayLocation=#{stayLocation}, stayType=#{stayType}, stayAdultPrice=#{stayAdultPrice}, stayKidPrice=#{stayKidPrice}, stayGrade=#{stayGrade}, stayPhone=#{stayPhone}, staySite=#{staySite}, stayTitle=#{stayTitle} where stayNum = #{stayNum}
