mapper.update:
update tour
set attReadCnt = attReadCnt + 1
where attNum = #{attNum}

update tour
set attName=#{attName} , attImage=#{attImage}
, attPhone=#{attPhone}, attLocation=#{attLocation}
, attAdultPrice=#{attAdultPrice},attKidPrice=#{attKidPrice},
 attTitle=#{attTitle}
, attContent=#{attContent},
attImageClone=#{attImageClone}
where attNum = #{attNum}
