mapper.select:
select count(*)
from tour

	
		attTitle LIKE '%'||#{searchValue}||'%'
	
	
		attName LIKE '%'||#{searchValue}||'%'
	
	
		and attLocation =#{attLocation}
	
	
		and attType =#{attType}

mapper.update:
update tour set
attImage=#{attImage}, attImageClone=#{attImageClone},
attContent=#{attContent}, attName=#{attName}, attLocation=#{attLocation}, attType=#{attType}, attAdultPrice=#{attAdultPrice}, attKidPrice=#{attKidPrice}, attPhone=#{attPhone}, attSite=#{attSite}, attTitle=#{attTitle}, attAddr1=#{attAddr1}, attAddr2=#{attAddr2} where attNum = #{attNum}
