mapper.select:
	select restnum, resttitle, restcontent, to_char(restwriteDay,'YYYY/MM/dd') restwriteDay, restreadCnt
    from restaurant
    where restnum = #{restnum}

mapper.update:
update restaurant
set restreadCnt = restreadCnt + 1
where restnum = #{restnum}

update restaurant
set resttitle=#{resttitle} , restcontent=#{restcontent}
where restnum = #{restnum}
